INSERT INTO `activities` VALUES ('15', '100002', '2', '穿山越岭到丽宫', '广东韶关', '韶关学院丁香苑c612', '/images/poster/2/3.jpg', '时间 : 12月06日 20:00 - 22:00\r\n地点 : 韶关 乳源周边\r\n\r\n活动介绍\r\n此行风雨无阻，请驴友自备雨具。\r\n	集合时间：2014.12.6 周六 20:00 （过时不候）\r\n	路线：紫荆桥头（南岸集合）～八一鲜明小学～水库～山路～丽宫后山～电视台～县城\r\n行程时间：预计2～3小时\r\n联系方式：第一峰户外QQ群 48691351\r\n对讲机频率：436.450\r\n装备要求：水、手电、徒步鞋（可选用装备：防蚊虫水、雨具、相机、登山杖、食物、个人药品）\r\n此活动非营利活动，参与者责任自负！\r\n详情查阅 第一峰户外 www.d1f.cn', '1', '1', '2014-10-16 22:14:52', '2014-10-29 12:00:00', '2014-12-14 22:30:00', '1', '0.665625', '0000');
